using namespace M4D::Imaging::Geometry;

SimpleMesh *
LoadMonkey()
{
	M4D::Imaging::Geometry::SimpleMesh *mesh = new M4D::Imaging::Geometry::SimpleMesh;

	mesh->ReserveVertices( VERTEX_COUNT );
	mesh->ReserveFaces( TRIANGLE_COUNT );
	for( unsigned i = 0; i < VERTEX_COUNT; ++i ) {
		SimpleVertex_f32 vertex;
		vertex.position = vertices[i]; 
		mesh->AddVertex( vertex );
	}

	for( unsigned i = 0; i < TRIANGLE_COUNT; ++i ) {
		SimpleTriangle triangle;
		triangle.indices = triangles[i];
		mesh->AddFace( triangle );
	}
	return mesh;
}
